import { Avatar, gloss, Row, Space, View } from '@o/ui'
import React from 'react'

import { colors } from '../../colors'
import { linkProps } from '../../useLink'
import { FadeChild } from '../../views/FadeInView'
import { TitleText } from '../../views/TitleText'
import { useScreenVal } from '../HomePage/SpacedPageContent'
import { BlogLayout } from './BlogLayout'
import { posts } from './posts'

export function BlogPageIndex() {
  const all = Object.keys(posts)
    .slice(0, 10)
    .map(id => ({
      ...posts[id],
      id,
    }))
    .filter(x => !x.private)
    .sort((a, b) => (new Date(a.date).getTime() > new Date(b.date).getTime() ? -1 : 1))

  return (
    <BlogLayout space>
      {all.map((post, index) => (
        <FadeChild key={post.date} delay={index * 150}>
          <Post
            padding={useScreenVal('md', 'xl', 'xl')}
            {...linkProps(`/blog/${all[index].id}`)}
            hoverStyle={{
              background: '#f9f9f9',
            }}
          >
            <TitleText
              fontWeight={200}
              color={colors.purple}
              selectable={false}
              textAlign="left"
              size="md"
            >
              {post.title}
            </TitleText>
            <Space size="sm" />
            <PostMeta post={post} />
          </Post>
        </FadeChild>
      ))}
    </BlogLayout>
  )
}

export const PostMeta = ({ post }) => {
  return (
    <Row alignItems="center" fontSize={16} alpha={0.65}>
      <Avatar size={16} src={post.authorImage} />
      &nbsp; &nbsp;
      {post.author}
      &nbsp;&nbsp;&middot;&nbsp;&nbsp;
      {new Date(post.date)
        .toLocaleDateString('en-US', {
          weekday: 'short',
          month: 'short',
          day: 'numeric',
        })
        .replace(/,.*,/, ',')
        .replace(/\//g, '·')}{' '}
    </Row>
  )
}

const Post = gloss(View, {})
