import { Col, Scale, Space, SurfacePassProps, Theme, View } from '@o/ui'
import { useWaitForFonts } from '@o/wait-for-fonts'
import React, { memo } from 'react'

import { fontProps } from '../../constants'
import { useScreenHeightVal, useScreenSize } from '../../hooks/useScreenSize'
import { fadeAnimations, FadeChild, useFadePage } from '../../views/FadeInView'
import { Paragraph } from '../../views/Paragraph'
import { TitleText } from '../../views/TitleText'
import { useTextFit } from '../../views/useTextFit'
import { Join } from './Join'
import { useScreenVal } from './SpacedPageContent'

let smallSpc = <Space size="xl" />
let medSpc = <Space size="xxl" />
let lgSpace = <Space size="xxxl" />

let allTitles = {
  large: 'A smart operating system for work',
  medium: 'A smart operating system for work',
  small: 'The data-driven app platform',
}

let allTexts = {
  large: [
    `Create apps for yourself, your team, or to sell to others.`,
    `The new app platform that gets out of your way.`,
  ],
  medium: [
    `Create apps for yourself, your team, or to sell to others.`,
    `The new app platform that gets out of your way.`,
  ],
  small: [`Build apps that power your company.`, `The data-first Work OS.`],
}

const HeadContent = memo(() => {
  const screen = useScreenSize()
  const fontsLoaded = useWaitForFonts(['Eesti Pro'])
  const measured = fontsLoaded
  const titleFit = useTextFit({ min: 16, updateKey: fontsLoaded })
  const pFit = useTextFit({ min: 16, updateKey: fontsLoaded })

  const texts = allTexts[screen]
  const longest = texts.reduce((a, c) => (a.length > c.length ? a : c), '')
  const br = useScreenVal(smallSpc, medSpc, lgSpace)

  const textsWidth = useScreenVal('95%', '100%', '90%')

  const isSmall = screen === 'small'

  return (
    <View
      className="head-text-section"
      width={useScreenVal('95%', '88%', '85%')}
      maxWidth={960}
      textAlign="center"
      alignItems="center"
    >
      <TitleText
        nodeRef={titleFit.ref}
        style={titleFit.style}
        fontWeight={100}
        alignSelf="center"
        transformOrigin="top center"
        selectable
        textAlign="center"
        whiteSpace="nowrap"
        maxHeight={80}
      >
        <FadeChild disable={!measured}>{allTitles[screen]}</FadeChild>
      </TitleText>

      <Space size={useScreenVal('md', 'xl', 'xxxl')} />

      {isSmall && (
        <Paragraph
          size={1.8}
          sizeLineHeight={1.6}
          margin={[0, 'auto']}
          textAlign="center"
          alpha={0.6}
          selectable
          zIndex={100}
        >
          {texts[0]} {texts[1]}
        </Paragraph>
      )}

      <View
        maxHeight={isSmall ? 0 : 'auto'}
        overflow={isSmall ? 'hidden' : 'visible'}
        position="relative"
        flex={1}
        width={textsWidth}
        margin={[0, 'auto']}
        maxWidth="80%"
      >
        <Paragraph
          fontWeight={400}
          tagName="div"
          style={{
            ...pFit.style,
            height: 'auto',
          }}
          lineHeight={pFit.isMeasured ? `${pFit.height}px` : `40px`}
          height="auto"
          transformOrigin="top left"
          margin={[0, 'auto']}
          textAlign="center"
          alpha={0.7}
          fontSize={40}
          whiteSpace="nowrap"
        >
          <FadeChild disable={!measured} delay={400}>
            {texts[0]}
          </FadeChild>
          {br}
          <FadeChild disable={!measured} delay={500}>
            {texts[1]}
          </FadeChild>
          {br}
          {texts[2] && (
            <>
              <FadeChild disable={!measured} delay={600}>
                {texts[2]}
              </FadeChild>
              {br}
            </>
          )}
          {/* <FadeChild {...fadeUpProps} disable={!measured} delay={650}>
            <Smaller {...linkProps('/apps#faq')}>{subTexts[screen]}</Smaller>
          </FadeChild> */}
        </Paragraph>

        {/* this is just to measure */}
        <Paragraph
          className="measure-p"
          nodeRef={pFit.ref}
          opacity={0}
          fontSize={40}
          position="absolute"
          whiteSpace="pre"
          pointerEvents="none"
        >
          {longest}
        </Paragraph>
      </View>
    </View>
  )
})

const HeadJoin = memo(() => {
  return (
    <View flex={1} width="100%" position="absolute" bottom="15%" alignItems="center">
      <FadeChild {...fadeAnimations.up} delay={500}>
        <SurfacePassProps elevation={5} {...fontProps.TitleFont}>
          <Theme name="orbitOneDark">
            <Scale size={useScreenVal(0.8, 1, 1.1)}>
              <Join
                inputProps={{
                  minWidth: useScreenVal('auto', 300, 300),
                  textAlign: 'left',
                }}
                borderRadius={1000}
                boxShadow={[[0, 5, 40, [0, 0, 0, 0.15]]]}
                flexFlow="row"
                group
                space={false}
                margin={[0, '-2%']}
              />
            </Scale>
          </Theme>
        </SurfacePassProps>
      </FadeChild>
      {/* <View
        position="absolute"
        overflow="hidden"
        backgroundImage={`url(${appScreen})`}
        backgroundSize="cover"
        backgroundPosition="center center"
        backgroundRepeat="no-repeat"
        borderRadius={10}
        width="100%"
        height="100%"
        zIndex={-1}
        boxShadow={[[0, 0, 100, [0, 0, 0]]]}
        pointerEvents="auto"
      /> */}
      {/* <DownloadButton
          onMouseEnter={() => setHoverDownload(true)}
          onMouseLeave={() => setHoverDownload(false)}
        /> */}
    </View>
  )
})

export function HeadSection() {
  const fontsLoaded = useWaitForFonts(['Eesti Pro'])
  const Fade = useFadePage({
    threshold: 0,
  })
  // const size = useScreenSize()

  return (
    <Fade.FadeProvide>
      <Col
        right={useScreenHeightVal(40, 0)}
        left={useScreenHeightVal(40, 0)}
        opacity={fontsLoaded ? 1 : 0}
        margin={['auto', 0]}
      >
        <Space size="xxxl" />
        <Col nodeRef={Fade.ref} margin={['auto', 0]} alignItems="center" justifyContent="center">
          <HeadContent />
        </Col>
        <Space size="xxxl" />
        <View flex={1} />
        <HeadJoin />
      </Col>
    </Fade.FadeProvide>
  )
}

